package guviTask;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		int c=40;
		int d=50;
		
		if((a+b)>(c+d))
		{
		System.out.println("the sum of a and b condition is not greater than the sum of c and d");
		}
		else
		{
			System.out.println("the sum of a and b condition is greater than the sum of c and d");
		}
	}

}
